 */
function playAnimation(id, animations){

  let index = 0
  let ele  = document.querySelector(id)
  setAnimation(ele,animations[index])
  ele.addEventListener('webkitAnimationEnd', function(){
    if(index < animations.length - 1){
      index ++
      setAnimation(ele,animations[index])
    }
  })
}

